package org.example;

import org.junit.jupiter.api.BeforeAll;
import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertEquals;

public class SolutionTest {

    static Solution solution;

    String s, t;
    char result;

    @BeforeAll
    static void setup(){

        solution = new Solution();
    }

    @Test
    public void emptyString(){

        s = "";
        t = "s";
        result = 's';

        assertEquals(result, solution.findTheDifference(s,t));

    }

    @Test
    public void OneCharacterString(){

        s = "s";
        t = "as";
        result = 'a';

        assertEquals(result, solution.findTheDifference(s,t));

    }

    @Test
    public void FiveCharacterString(){

        s = "suars";
        t = "sueasr";
        result = 'e';

        assertEquals(result, solution.findTheDifference(s,t));

    }

    @Test
    public void FiveCharacterStringLastCharacterDifferent(){

        s = "suars";
        t = "suarse";
        result = 'e';

        assertEquals(result, solution.findTheDifference(s,t));

    }

    @Test
    public void ALotOfCharacterString(){

        s = "";
        t = "";
        result = 't';

        assertEquals(result, solution.findTheDifference(s,t));

    }
}
